mapper.update:
UPDATE C##NUMBERONE.BOARD
SET READ_CNT = NVL(READ_CNT, 0) + 1
WHERE BOARD_NO = #{boardNo}
AND   REG_ID  <>  #{regId}

UPDATE C##NUMBERONE.BOARD
   SET ask_cnt = NVL(ASK_CNT, 0) + 1
 WHERE BOARD_NO = #{boardNo}

mapper.select:
SELECT A.*, B.* FROM ( SELECT TT1.RNUM AS no, TT1.TITLE, TT1.READ_CNT as readCnt, TT1.REG_ID as regId, TO_CHAR(TT1.REG_DT, 'YYYY/MM/DD HH24:MI:SS') as regDt, TO_CHAR(TT1.MOD_DT, 'YYYY/MM/DD HH24:MI:SS') as modDt, TT1.BOARD_NO as boardNo FROM ( SELECT ROWNUM AS RNUM, T1.* FROM ( SELECT * FROM C##NUMBERONE.BOARD WHERE 1=1
ORDER BY MOD_DT DESC ) T1 WHERE ROWNUM <= (#{pageSize} * (#{pageNo} - 1) + #{pageSize}) ) TT1 WHERE TT1.RNUM >= (#{pageSize} * (#{pageNo} - 1) + 1) ) A CROSS JOIN ( SELECT COUNT(*) totalCnt FROM C##NUMBERONE.BOARD WHERE 1=1
) B
