mapper.select:
select count(*) from message_area where area_code IN (
		        SELECT loc_code 
		        FROM location 
		        START WITH loc_code = #{locCode}
		        CONNECT BY PRIOR parent_loc_code = loc_code
		    ) and message_seq > (select max(sequence_number) from sequence_tracker)

choose.when:
        message_seq In (SELECT DISTINCT message_seq
FROM message_area
WHERE area_code =#{searchWord}
) and MSG_REG_DT between #{startDate} and #{endDate}

mapper.select:
SELECT A.*,B.* FROM( SELECT TT1.RNUM AS no, TT1.message_seq as messageSeq, (SELECT DET_CODE_NM from code where mst_code ='DISASTER_TYPE' and DET_CODE = TT1.disaster_type) as disasterType, --TT1.disaster_type as disasterType, (SELECT DET_CODE_NM from code where mst_code ='EMERGENCY_LEVEL' and DET_CODE = TT1.emergency_level) as emergencyLevel, --TT1.emergency_level as emergencyLevel, TT1.message_context as messageContext, TT1.broadcast_organization as broadcastOrganization, DECODE( TO_CHAR(TT1.msg_reg_dt,'YYYYMMDD'), TO_CHAR(SYSDATE,'YYYYMMDD'), TO_CHAR(TT1.msg_reg_dt,'HH24:MI'),TO_CHAR(TT1.msg_reg_dt,'YYYY/MM/DD') ) AS msgRegDt FROM ( SELECT ROWNUM AS RNUM, T1.* FROM ( SELECT * FROM disasterMSG --WHERE default : all, not null : locCode
ORDER BY message_seq DESC ) T1 WHERE ROWNUM <= ( #{pageSize}*(#{pageNo} -1) + #{pageSize} ) )TT1 WHERE tt1.rnum >=( #{pageSize}*(#{pageNo} -1) + 1 ) )A CROSS JOIN( SELECT COUNT(*) totalCnt FROM disasterMSG
)B
